class WrongNumberOfPlayersError < StandardError ; endclass NoSuchStrategyError < StandardError ; end   def rps_game_winner(game) raise WrongNumberOfPlayersError unless game.length == 2    game.each{|key, value|  raise NoSuchStrategyError unless value[(/[RPS]/)] }    if (game[1][1]==game[0][1])        return nill    else        if ((game[0][1]=="P" and game[1][1]=="R") or (game[0][1]=="S" and game[1][1]=="P") or (game[0][1]=="R" and game[1][1]=="S"))            return game[0]            else            return game[1]        end    endend     def rps_tournament_winner(tournament)        if tournament[0][0].class==String            return rps_game_winner(tournament)        end        array = [rps_tournament_winner(tournament[0]), rps_tournament_winner(tournament[1])]        rps_game_winner(array)    end    